import { createSlice } from "@reduxjs/toolkit";


const initialState = {items:[], cart:false, cartItems: 0};
const mainCartSlice = createSlice({
    name: "shopcart",
    initialState,
    reducers:{
        setShowCart(state){
            state.cart = !state.cart;
        },
        itemsInCart(state, action){
            const newItem = action.payload;
            console.log(newItem);
            const exitingItem = state.items.map(item =>item.id === newItem.id);
            console.log(exitingItem);
            if(JSON.stringify(exitingItem) === "{}"){
                state.items.push(newItem);
                console.log(state.items)
            }
            else{
                state.items.map(item =>{
                    if (item.id === newItem.id){
                        item.quantity = item.quantity + newItem.quantity;
                    }}
                    );
            }
        },
    }

});

export default mainCartSlice;
